	<span class="token string">"^M"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">13</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^N"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">14</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^O"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">15</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^P"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">16</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^Q"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">17</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^R"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">18</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^S"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">19</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^T"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">20</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^U"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">21</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^V"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">22</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
