	<span class="token string">"^X"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">24</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"^Y"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">25</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
