	<span class="token string">"LF"</span><span class="token punctuation">:</span>       <span class="token punctuation">{</span><span class="token number">10</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"CR"</span><span class="token punctuation">:</span>       <span class="token punctuation">{</span><span class="token number">13</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"CRLF"</span><span class="token punctuation">:</span>     <span class="token punctuation">{</span><span class="token number">13</span><span class="token punctuation">,</span> <span class="token number">10</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"ESC"</span><span class="token punctuation">:</span>      <span class="token punctuation">{</span><span class="token number">27</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"ESCAPE"</span><span class="token punctuation">:</span>   <span class="token punctuation">{</span><span class="token number">27</span><span class="token punctuation">}</span><span class="token punctuation">,</span>
	<span class="token string">"BS-ASCII"</span><span class="token punctuation">:</span> <span class="token punctuation">{</span><span class="token number">127</span><span class="token punctuation">}</span><span class="token punctuation">,</span>

	<span class="token comment">// ansi escape sequences</span>
